mapper.delete:
	 update PROJECT_DATA_DICTIONARY 
	               set DEL_FLAG = #{DEL_FLAG}
 	  where 
	     DATA_ID = #{DATA_ID:VARCHAR}

mapper.update:
update PROJECT_DATA_DICTIONARY_DTL
PAR_DATA_DTL_NAME = #{PAR_DATA_DTL_NAME} , DATA_DTL_NAME = #{DATA_DTL_NAME} , DATA_DTL_CODE = #{DATA_DTL_CODE} , PAR_DATA_DTL_ID = #{PAR_DATA_DTL_ID} , PAR_DATA_DTL_CODE = #{PAR_DATA_DTL_CODE} , DATA_DTL_DES_1 = #{DATA_DTL_DES_1} , DATA_DTL_DES_2 = #{DATA_DTL_DES_2} , REMARK = #{REMARK} ,
where DATA_DTL_ID = #{DATA_DTL_ID}

mapper.delete:
	 update PROJECT_DATA_DICTIONARY_DTL 
	               set DEL_FLAG = #{DEL_FLAG}	 
 	  where 
	      DATA_ID = #{DATA_ID:VARCHAR}
