table.pyName:
(select MARKETING_CARD_ID,MARKETING_CARD_NO,CARD_TYPE,CARD_VALUE,
			to_char(CRE_TIME,'yyyy-MM-DD')CRE_TIME,CARD_SEQ_NO,STATE,DEL_FLAG,MARKETING_ACT_ID
			from ERP_MARKETING_CARD where DEL_FLAG=0 )

(select u.*,
(select sum(g.PAYABLE_AMOUNT) GIFT_AMOUNT from ERP_ADVC_GIFT_DTL g where g.del_flag=0 and g.ADVC_ORDER_ID=u.ADVC_ORDER_ID)GIFT_AMOUNT
from ERP_ADVC_ORDER u )
