table.pyName:
(select a.WLXXID, a.WLBH, a.WLMC, a.WLXXYSID, a.YSBH, a.YSMC, a.PP, a.NF, a.JJ, (NVL(a.XQSL,0)- NVL(a.QGSL,0)) as XQSL, 
        to_char(a.XDRQ,'YYYY-MM-DD')XDRQ, to_char(a.SQXQRQ,'YYYY-MM-DD')SQXQRQ, to_char(a.CNJQ,'YYYY-MM-DD')CNJQ, a.CPXQJHID, a.delflag, a.CPXQJHMXID,b.LBM 
        from T_ERP_CG_CPXQJHMX a left join T_ERP_JC_WLXX b on a.WLXXID=b.WLXXID where to_number(NVL(a.XQSL,0)) >  to_number(NVL(a.QGSL,0)))

( select KFBH,KFMC,KFXXID,WLDWXXID,WLDWBH,WLDWMC,SQJCRYID,SQJCRYMC,CLWZJCDBH,bbb.CLWZJCDID  from T_ERP_KC_CLWZJCD bbb
    where bbb.clwzjcdid in (select distinct a.clwzjcdid
    from T_ERP_KC_CLWZJCDMX a where (nvl(a.bzsjjcsl,0) - nvl(a.yhhsl,0)) > 0 and a.delflag=0)  and bbb.delflag=0 and bbb.state='已借出')

(select a.*, ( case when b.FLAG=1 and SLSLS=0 then '未收料' else (case when b.FLAG=2 then '部分收料' else (case when b.FLAG=0 then '已收料' else (case when b.FLAG=1 and SLSLS>0 then '部分收料' end) end) end) end) STATUS,FLAG from T_ERP_CG_CLWZCGDD a, (select CLWZCGDDID, max((case when NVL(SLSL, 0) > 0 and NVL(CGSL, 0) > NVL(SLSL, 0) then 2 else (case when NVL(SLSL, 0) = 0 then 1
else (case when NVL(CGSL, 0) = NVL(SLSL, 0) then 0
end )
end) end)) FLAG,sum(NVL(CGSL, 0)) CGSLS, sum(NVL(SLSL, 0))SLSLS from T_ERP_CG_CLWZCGDDMX where DELFLAG=0 group by CLWZCGDDID) B where A.CLWZCGDDID=B.CLWZCGDDID)

(select QYID,QYBH,QYMC  from t_erp_xs_qy where qyid not in (
              select nvl(sjqyid,'aaa') from t_erp_xs_qy where delflag = 0
	        )and delflag = 0 and STATE='启用')

(select ZXZXMXID as ZXMXID,XMCZID,ZXXHID as XHID,WLXXYSID,WLZHID,WLXXID,PP,NF,JJ,WLBH,WLMC,YSMC,CM,CMSL
  	                                  from T_ERP_KC_ZXZXMX where delflag=0
  	                               )

(select a.CPKCXMMXSSZID,a.XMBH,a.kfxxid,a.kwxxid from T_ERP_JZ_CPKCXMMXSSZ a where (KCZTBJ != 1 or KCZTBJ is null)
  	                           and CPKCXMMXSSZID not in (select CXXHID from T_ERP_KC_CXXH where DELFLAG=0)
  	                         )
